import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';

const SpeechToText = () => {
  const { sessionId } = useParams();
  const [text, setText] = useState([]);
  const [isListening, setIsListening] = useState(false);
  const recognitionRef = useRef(null);
  const socketRef = useRef(null);
  const [interimTranscript, setInterimTranscript] = useState('');

  useEffect(() => {
    socketRef.current = new WebSocket(`ws://localhost:5000?session=${sessionId}`);

    socketRef.current.onmessage = async (event) => {
      try {
        const data = await event.data.text(); // Convert Blob to text
        const receivedText = JSON.parse(data); // Parse the JSON text
        setText((prevText) => [...prevText, receivedText]);
      } catch (error) {
        console.error('Error parsing message:', error);
      }
    };

    return () => {
      socketRef.current.close();
    };
  }, [sessionId]);

  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recog = new SpeechRecognition();
      recog.continuous = true;
      recog.interimResults = true;
      recog.lang = 'en-IN';

      recog.onresult = (event) => {
        let finalTranscript = '';
        let interimTranscript = '';

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript.trim();
          if (event.results[i].isFinal) {
            finalTranscript += transcript + ' ';
          } else {
            interimTranscript += transcript + ' ';
          }
        }

        setInterimTranscript(interimTranscript);

        if (finalTranscript) {
          setText((prevText) => [...prevText, finalTranscript]);
          setInterimTranscript('');
          socketRef.current.send(JSON.stringify(finalTranscript));
        }
      };

      recog.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      recog.onend = () => {
        if (isListening) {
          recog.start();
        }
      };

      recognitionRef.current = recog;
    } else {
      alert('Your browser does not support speech recognition.');
    }
  }, [isListening]);

  const startListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  return (
    <div>
      <button onClick={isListening ? stopListening : startListening}>
        {isListening ? 'Stop Listening' : 'Start Listening'}
      </button>
      <div>
        {text.map((line, index) => (
          <p key={index}>{line}</p>
        ))}
        {interimTranscript && <p style={{ color: 'gray' }}>{interimTranscript}</p>}
      </div>
    </div>
  );
};

export default SpeechToText;
